/**
 * @internal
 **/
export function toV12(registry, {
  extrinsic,
  modules
}) {
  return registry.createType('MetadataV12', {
    extrinsic,
    modules: modules.map(mod => registry.createType('ModuleMetadataV12', { ...mod,
      index: 255
    }))
  });
}